INSERT INTO `rk_users` (`ID`, `user_login`, `user_pass`, `user_nicename`, `user_email`, `user_url`, `user_registered`, `user_activation_key`, `user_status`, `display_name`) VALUES
(1, 'admin', '$P$BeMQK79BXvTu3wlQQDuYUPQD72IAM41', 'admin', 'handel@handel.com', 'http://localhost/handel', '2020-10-20 20:19:10', '', 0, 'admin');
INSERT INTO `rk_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`) VALUES
(1, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Dicas, atualizações de produtos e inspiração', 'Estamos aqui para você, receba dicas, atualizações de produtos e inspiração diretamente na sua caixa de e-mail', '{}', 'unactioned', 'woocommerce-admin', '2020-10-21 18:49:44', NULL, 0, 'plain', '', 0, 'info'),
(2, 'wc-admin-marketing-intro', 'info', 'en_US', 'Conecte-se com seu público', 'Aumente sua base de clientes e aumente suas vendas com as ferramentas de marketing criadas para o WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-21 18:49:44', NULL, 0, 'plain', '', 0, 'info'),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Conectar ao WooCommerce.com', 'Conecte-se para receber notificações e atualizações importantes do produto.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-21 18:49:44', NULL, 0, 'plain', '', 0, 'info'),
(4, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-10-26 18:49:44', NULL, 0, 'plain', '', 0, 'info'),
(5, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Boas-vindas ao WooCommerce! Configure sua loja e comece a vender', 'Estamos aqui para ajudá-lo a executar as etapas mais importantes para colocar sua loja em funcionamento.', '{}', 'actioned', 'woocommerce-admin', '2020-10-22 10:49:44', NULL, 0, 'plain', '', 0, 'info'),
(6, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2020-10-26 18:49:44', NULL, 0, 'plain', '', 0, 'info'),
(7, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-10-26 18:49:44', NULL, 0, 'plain', '', 0, 'info'),
(8, 'wc-admin-draw-attention', 'info', 'en_US', 'Como chamar a atenção para sua loja online', 'Para começar, aqui estão sete maneiras de aumentar suas vendas e evitar se afogar em produtos similares produzidos em massa que competem pelos mesmos compradores.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-21 18:59:02', NULL, 0, 'plain', '', 0, 'info'),
(9, 'wc-admin-mobile-app', 'info', 'en_US', 'Instale o aplicativo Woo', 'Instale o aplicativo do WooCommerce para gerenciar pedidos, receber notificações de vendas e visualizar as principais métricas, onde quer que você esteja.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-27 22:55:27', NULL, 0, 'plain', '', 0, 'info'),
(10, 'wc-admin-launch-checklist', 'info', 'en_US', 'Pronto para lançar sua loja?', 'Para garantir que você nunca fique com aquela sensação de \"o que eu esqueci\", reunimos uma lista com tudo que é essencial para você verificar antes do lançamento.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-27 22:55:27', NULL, 0, 'plain', '', 0, 'info'),
(11, 'wc-admin-need-some-inspiration', 'info', 'en_US', 'Você precisa de alguma inspiração?', 'Confira algumas de nossas histórias favoritas de clientes de empreendedores, agências e desenvolvedores em nossa comunidade global.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-27 22:55:27', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wc-admin-online-clothing-store', 'info', 'en_US', 'Inicie sua loja de roupas online', 'Iniciar um site de moda é empolgante, mas também pode parecer esmagador. Neste artigo, orientaremos você no processo de configuração, ensinaremos a criar listas bem-sucedidas de produtos e mostraremos como comercializar para seu público-alvo ideal.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-27 22:55:27', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Ajude o WooCommerce a melhorar ativando o rastreamento de uso.', 'A coleta de dados de uso nos permite melhorar o WooCommerce. Sua loja será considerada à medida que avaliarmos novos recursos, julgarmos a qualidade de uma atualização ou determinarmos se uma melhoria faz sentido. Você sempre pode visitar as <a href=\"http://localhost/handel/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Configurações</a> e optar por parar de compartilhar dados. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Saiba mais</a> sobre quais dados coletamos.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-28 21:18:14', NULL, 0, 'plain', '', 0, 'info'),
(14, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Dê sua opinião', 'Agora que você nos escolheu como parceiro, nosso objetivo é garantir que estamos fornecendo as ferramentas certas para atender às suas necessidades. Esperamos receber suas opiniões sobre a experiência de configuração da loja para que possamos melhorá-la no futuro.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-29 18:50:09', NULL, 0, 'plain', '', 0, 'info'),
(15, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Você sabia?', 'Uma loja com tecnologia WooCommerce precisa, em média, de 31 dias para obter a primeira venda. Você está no caminho certo! Você acha esse tipo de insight útil?', '{}', 'unactioned', 'woocommerce-admin', '2020-10-29 18:50:09', NULL, 0, 'plain', '', 0, 'info'),
(16, 'wc-admin-home-screen-feedback', 'info', 'en_US', 'Ajude-nos a melhorar a tela inicial do WooCommerce', 'Gostaríamos muito da sua opinião para moldar o futuro da tela inicial do WooCommerce. Sinta-se livre para compartilhar qualquer opinião, ideia ou sugestão que você tiver.', '{}', 'unactioned', 'woocommerce-admin', '2020-11-03 12:10:59', NULL, 0, 'plain', '', 0, 'info'),
(17, 'wc-admin-home-screen-feedback', 'info', 'en_US', 'Ajude-nos a melhorar a tela inicial do WooCommerce', 'Gostaríamos muito da sua opinião para moldar o futuro da tela inicial do WooCommerce. Sinta-se livre para compartilhar qualquer opinião, ideia ou sugestão que você tiver.', '{}', 'unactioned', 'woocommerce-admin', '2020-11-03 12:10:59', NULL, 0, 'plain', '', 0, 'info'),
(18, 'wc-admin-customize-store-with-blocks', 'info', 'en_US', 'Personalize sua loja online com os blocos do WooCommerce', 'Com nossos blocos, você pode selecionar e exibir produtos, categorias, filtros e mais praticamente em qualquer lugar do seu site, não é necessário usar códigos de acesso nem editar linhas de código. Saiba mais sobre como usar cada um deles.', '{}', 'unactioned', 'woocommerce-admin', '2020-11-04 18:52:16', NULL, 0, 'plain', '', 0, 'info');
